property.setter:
var boxy = document.getAnonymousElementByAttribute(gURLBar.popup, 'anonid', 'richlistbox');
var rows = boxy.getElementsByClassName('autocomplete-richlistitem');

if (!rows || !rows.length) return;

// 1. if anything's selected, unselect it
Array.prototype.forEach.call(rows, function(row, i) {
  row.selected = false;
});

// 2. select the specified value, unless it's -1 or off the end of the array
if (val > -1) {
  if (val > rows.length - 1) {
    val = rows.length - 1;
  }
  rows[val].selected = true;
  // scroll the row into view, if necessary
  boxy.ensureIndexIsVisible(val);
  return val;
  this._selectedIndex = val;
}
return -1;
this._selectedIndex = -1;

method.body:
// divide out maxRow if necessary, could be an off-by-one error here
let realAmount = amount % maxRow;
this._selectedIndex = reverse ? this._selectedIndex - realAmount: this._selectedIndex + realAmount;
return this._selectedIndex;

handlers.handler:
// If the urlbar handles the event, it returns true. Else, it returns
// false, and the existing XBL key handlers handle the key event.
return window.universalSearch.urlbar.onKeyPress(event) || this.handleKeyPress(event);
